                        <div class="width-half column-gap-50">
                            <div class="">
                                <b-form-select class="form-select"
                                               placeholder="Выберите категорию"
                                               :options="category">
                                    <!--                                    <option value="1" selected>Выберите категорию</option>-->
                                    <!--                                    <option value="2">Категория 1</option>-->
                                    <!--                                    <option value="3">Категория 2</option>-->
